sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (newValue === '') {
      return;
   }

	var whatneeded = g_form.getValue('u_oque_e_necessario');
   //Type appropriate comment here, and begin script below
   //limpa o campo o que eh necessario
   g_form.clearOptions('u_oque_e_necessario');
	//se em tipo solicitação for selecionado RH em o que é necessario carregara as opções abaixo
	if(newValue == 'rh'){
		g_form.addOption('u_oque_e_necessario','rh1', 'Recursos Humano 1');
		g_form.addOption('u_oque_e_necessario','rh2', 'Recursos Humano 2');
		g_form.addOption('u_oque_e_necessario','outro', 'Outro');
	}
	//se em tipo solicitação for selecionado Instalações em o que é necessario carregara as opções abaixo
	if(newValue == 'instalacoes'){
		g_form.addOption('u_oque_e_necessario','instalacoes1', 'Instalação 1');
		g_form.addOption('u_oque_e_necessario','instalacoes2', 'Instalação 2');
		g_form.addOption('u_oque_e_necessario','outro', 'Outro');
	}
	//se em tipo solicitação for selecionado Legal em o que é necessario carregara as opções abaixo
	if(newValue == 'legal'){
		g_form.addOption('u_oque_e_necessario','legal1', 'Legal 1');
		g_form.addOption('u_oque_e_necessario','legal2', 'Legal 2');
		g_form.addOption('u_oque_e_necessario','outro', 'Outro');
	}
	if(isLoading && !g_form.isNewRecord()){
			g_form.setValue('u_oque_e_necessario', whatneeded);
		}
}
